import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import axios from 'axios'

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      ancedotes: [],
      current: 0
    }
  }

  componentDidMount = () => {
    axios
      .get('http://localhost:3001/anecdotes')
      .then( response => {
        this.setState({'anecdnotes': response.data})
      })
  }

  render() {
    if (this.state.ancedotes.length === 0) {
      return <div>No ancedotes...</div>
    }
    return (
      <div>
        <h1>anecdote of the day</h1>
        <div>{this.state.ancedotes[this.state.current].content}</div>
        <button>next</button>
      </div>
    )
  }
}

export default App


